Insert into MESSAGES (SENDERID,RECIPIENTID,SUBJECT,DATE_SENT,MESSAGE_READ,TEXT) values (-2147483384,-2147483193,'kilmo about the NSA',to_timestamp('14/06/02 07:45:47,000000000','DD/MM/RR HH24:MI:SS,FF'),'1','
            
                
