Insert into MESSAGES (SENDERID,RECIPIENTID,SUBJECT,DATE_SENT,MESSAGE_READ,TEXT) values (-2147483486,-2147483485,'More Advanced than CVS',to_timestamp('11/07/01 05:49:54,000000000','DD/MM/RR HH24:MI:SS,FF'),'1','
