Insert into MESSAGES (SENDERID,RECIPIENTID,SUBJECT,DATE_SENT,MESSAGE_READ,TEXT) values (-2147483307,-2147483367,'A Jewish Deduction',to_timestamp('19/03/02 03:56:58,000000000','DD/MM/RR HH24:MI:SS,FF'),'1','
