Insert into MESSAGES (SENDERID,RECIPIENTID,SUBJECT,DATE_SENT,MESSAGE_READ,TEXT) values (-2147483186,-2147483502,'Microsoft Editing Macros',to_timestamp('21/12/08 21:32:28,000000000','DD/MM/RR HH24:MI:SS,FF'),'1','

            
