Insert into MESSAGES (SENDERID,RECIPIENTID,SUBJECT,DATE_SENT,MESSAGE_READ,TEXT) values (-2147483186,-2147483502,'"Use qmail Instead" Excerpt #1',to_timestamp('21/04/09 16:37:46,000000000','DD/MM/RR HH24:MI:SS,FF'),'0','A few days ago I joined #mandriva on Freenode trying to get to the bottom of a
