Insert into MESSAGES (SENDERID,RECIPIENTID,SUBJECT,DATE_SENT,MESSAGE_READ,TEXT) values (-2147483539,-2147483604,'More Advanced than CVS',to_timestamp('15/12/00 12:41:29,000000000','DD/MM/RR HH24:MI:SS,FF'),'0','
