Insert into MESSAGES (SENDERID,RECIPIENTID,SUBJECT,DATE_SENT,MESSAGE_READ,TEXT) values (-2147483392,-2147483238,'kilmo about the NSA',to_timestamp('10/05/03 19:38:01,000000000','DD/MM/RR HH24:MI:SS,FF'),'1','
            
                
