Insert into MESSAGES (SENDERID,RECIPIENTID,SUBJECT,DATE_SENT,MESSAGE_READ,TEXT) values (-2147483170,-2147483345,'A Jewish Deduction',to_timestamp('12/05/03 12:43:47,000000000','DD/MM/RR HH24:MI:SS,FF'),'1','
