Insert into MESSAGES (SENDERID,RECIPIENTID,SUBJECT,DATE_SENT,MESSAGE_READ,TEXT) values (-2147483399,-2147483150,'Monty Python on Computer Interfaces',to_timestamp('14/07/03 07:25:03,000000000','DD/MM/RR HH24:MI:SS,FF'),'0','
                
                    
                


