Insert into MESSAGES (SENDERID,RECIPIENTID,SUBJECT,DATE_SENT,MESSAGE_READ,TEXT) values (-2147483575,-2147483315,'#python on Incrementing',to_timestamp('18/01/01 21:17:40,000000000','DD/MM/RR HH24:MI:SS,FF'),'0','
