Insert into MESSAGES (SENDERID,RECIPIENTID,SUBJECT,DATE_SENT,MESSAGE_READ,TEXT) values (-2147483262,-2147483150,'kilmo about the NSA',to_timestamp('23/11/07 17:26:35,000000000','DD/MM/RR HH24:MI:SS,FF'),'1','
            
                
