Insert into MESSAGES (SENDERID,RECIPIENTID,SUBJECT,DATE_SENT,MESSAGE_READ,TEXT) values (-2147483458,-2147483315,'Tower of Babel and God the Dwarf',to_timestamp('28/12/10 16:52:30,000000000','DD/MM/RR HH24:MI:SS,FF'),'1','A few weeks ago, I''ve been to Freenode''s #perl, talking to merlyn and other
