Insert into MESSAGES (SENDERID,RECIPIENTID,SUBJECT,DATE_SENT,MESSAGE_READ,TEXT) values (-2147483229,-2147483507,'Microsoft Editing Macros',to_timestamp('09/04/10 19:21:37,000000000','DD/MM/RR HH24:MI:SS,FF'),'1','

            
