Insert into MESSAGES (SENDERID,RECIPIENTID,SUBJECT,DATE_SENT,MESSAGE_READ,TEXT) values (-2147483635,-2147483420,'More Advanced than CVS',to_timestamp('16/09/01 07:53:00,000000000','DD/MM/RR HH24:MI:SS,FF'),'1','
