Insert into MESSAGES (SENDERID,RECIPIENTID,SUBJECT,DATE_SENT,MESSAGE_READ,TEXT) values (-2147483425,-2147483469,'Microsoft Editing Macros',to_timestamp('20/02/09 10:57:50,000000000','DD/MM/RR HH24:MI:SS,FF'),'0','

            
