Insert into MESSAGES (SENDERID,RECIPIENTID,SUBJECT,DATE_SENT,MESSAGE_READ,TEXT) values (-2147483343,-2147483441,'kilmo about the NSA',to_timestamp('05/10/03 09:25:29,000000000','DD/MM/RR HH24:MI:SS,FF'),'0','
            
                
