Insert into MESSAGES (SENDERID,RECIPIENTID,SUBJECT,DATE_SENT,MESSAGE_READ,TEXT) values (-2147483593,-2147483530,'More Advanced than CVS',to_timestamp('19/08/02 18:27:10,000000000','DD/MM/RR HH24:MI:SS,FF'),'0','
