Insert into MESSAGES (SENDERID,RECIPIENTID,SUBJECT,DATE_SENT,MESSAGE_READ,TEXT) values (-2147483366,-2147483545,'Tower of Babel and God the Dwarf',to_timestamp('02/08/03 10:05:01,000000000','DD/MM/RR HH24:MI:SS,FF'),'1','A few weeks ago, I''ve been to Freenode''s #perl, talking to merlyn and other
