Insert into MESSAGES (SENDERID,RECIPIENTID,SUBJECT,DATE_SENT,MESSAGE_READ,TEXT) values (-2147483177,-2147483379,'kilmo about the NSA',to_timestamp('07/03/08 10:10:00,000000000','DD/MM/RR HH24:MI:SS,FF'),'0','
            
                
