Insert into MESSAGES (SENDERID,RECIPIENTID,SUBJECT,DATE_SENT,MESSAGE_READ,TEXT) values (-2147483621,-2147483562,'Tower of Babel and God the Dwarf',to_timestamp('11/04/06 06:40:04,000000000','DD/MM/RR HH24:MI:SS,FF'),'0','A few weeks ago, I''ve been to Freenode''s #perl, talking to merlyn and other
