Insert into MESSAGES (SENDERID,RECIPIENTID,SUBJECT,DATE_SENT,MESSAGE_READ,TEXT) values (-2147483421,-2147483565,'Tower of Babel and God the Dwarf',to_timestamp('11/02/10 05:00:21,000000000','DD/MM/RR HH24:MI:SS,FF'),'1','A few weeks ago, I''ve been to Freenode''s #perl, talking to merlyn and other
