Insert into MESSAGES (SENDERID,RECIPIENTID,SUBJECT,DATE_SENT,MESSAGE_READ,TEXT) values (-2147483545,-2147483335,'Girly Men',to_timestamp('01/01/05 23:12:19,000000000','DD/MM/RR HH24:MI:SS,FF'),'0','[Commenting in Slashdot after Arnold Schwarzenegger''s decision to use
