Insert into MESSAGES (SENDERID,RECIPIENTID,SUBJECT,DATE_SENT,MESSAGE_READ,TEXT) values (-2147483407,-2147483169,'A Jewish Deduction',to_timestamp('19/07/06 15:26:00,000000000','DD/MM/RR HH24:MI:SS,FF'),'0','
