Insert into MESSAGES (SENDERID,RECIPIENTID,SUBJECT,DATE_SENT,MESSAGE_READ,TEXT) values (-2147483559,-2147483628,'Microsoft Editing Macros',to_timestamp('12/12/02 20:04:20,000000000','DD/MM/RR HH24:MI:SS,FF'),'1','

            
