Insert into MESSAGES (SENDERID,RECIPIENTID,SUBJECT,DATE_SENT,MESSAGE_READ,TEXT) values (-2147483140,-2147483279,'kilmo about the NSA',to_timestamp('21/05/04 03:35:02,000000000','DD/MM/RR HH24:MI:SS,FF'),'1','
            
                
