Insert into MESSAGES (SENDERID,RECIPIENTID,SUBJECT,DATE_SENT,MESSAGE_READ,TEXT) values (-2147483601,-2147483449,'"Use qmail Instead" Excerpt #1',to_timestamp('10/08/09 00:29:55,000000000','DD/MM/RR HH24:MI:SS,FF'),'1','A few days ago I joined #mandriva on Freenode trying to get to the bottom of a
